"use client"

import { useState, useEffect } from "react"

export default function CampaignMap() {
  const [isMounted, setIsMounted] = useState(false)

  useEffect(() => {
    setIsMounted(true)
  }, [])

  if (!isMounted) {
    return (
      <div className="h-[400px] rounded-lg border bg-card flex items-center justify-center">
        <div className="text-muted-foreground">Loading map...</div>
      </div>
    )
  }

  return (
    <div className="h-[400px] rounded-lg border bg-card overflow-hidden relative">
      {/* This is a placeholder for a real map. In a production app, you would integrate with a mapping library */}
      <div className="absolute inset-0 bg-[#f0f0f0] flex flex-col items-center justify-center p-4">
        <div className="text-center mb-4">
          <h3 className="font-semibold text-lg">Pune Mobile Billboard Route</h3>
          <p className="text-sm text-muted-foreground">Interactive map showing optimal locations</p>
        </div>

        <div className="relative w-full max-w-md h-64">
          {/* Simplified map representation */}
          <div className="absolute inset-0 bg-[#e0e0e0] rounded-lg">
            {/* Main roads */}
            <div className="absolute top-1/2 left-0 right-0 h-1 bg-[#c0c0c0]"></div>
            <div className="absolute top-0 bottom-0 left-1/2 w-1 bg-[#c0c0c0]"></div>

            {/* Location markers */}
            <div className="absolute top-[20%] left-[30%] w-3 h-3 bg-primary rounded-full" title="FC Road"></div>
            <div className="absolute top-[40%] left-[60%] w-3 h-3 bg-primary rounded-full" title="Koregaon Park"></div>
            <div className="absolute top-[70%] left-[40%] w-3 h-3 bg-primary rounded-full" title="MG Road"></div>
            <div className="absolute top-[30%] left-[80%] w-3 h-3 bg-primary rounded-full" title="Viman Nagar"></div>
            <div className="absolute top-[50%] left-[70%] w-3 h-3 bg-primary rounded-full" title="Kalyani Nagar"></div>
            <div
              className="absolute top-[10%] left-[50%] w-3 h-3 bg-primary rounded-full"
              title="Pune University"
            ></div>
            <div className="absolute top-[80%] left-[70%] w-3 h-3 bg-primary rounded-full" title="Hadapsar"></div>
            <div className="absolute top-[25%] left-[15%] w-3 h-3 bg-primary rounded-full" title="Baner"></div>
          </div>
        </div>

        <div className="mt-4 text-sm text-center text-muted-foreground">
          Note: This is a simplified representation. In a real implementation, this would be an interactive map with
          precise routes and locations.
        </div>
      </div>
    </div>
  )
}
