"use client"
import { Calendar, Check, Clock, DollarSign, MapPin, RouteIcon, Target, TrendingUp } from "lucide-react"
import { useEffect, useState } from "react"
import { Card } from "../components/ui/card"
import { Tabs } from "../components/ui/tabs"

// Import the components we'll create next
import BudgetBreakdown from "../components/BudgetBreakdown"
import CampaignMap from "../components/CampaignMap"
import DebugPanel from "../components/DebugPanel"
import RouteTimeline from "../components/RouteTimeline"

export default function CampaignResults() {
  const [campaignData, setCampaignData] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    // Start with loading state
    setLoading(true);
    
    // Check if we're in preview mode from URL parameter
    const searchParams = new URLSearchParams(window.location.search);
    const isPreview = searchParams.get('preview') === 'true';
    
    // Initialize state variables
    let currentCampaignData = null;
    
    try {
      if (isPreview) {
        // Get the preview analytics data
        const previewAnalyticsJSON = localStorage.getItem("previewAnalytics");
        if (previewAnalyticsJSON) {
          const previewAnalytics = JSON.parse(previewAnalyticsJSON);
          
          // Get the user's form data
          const tempFormDataJSON = localStorage.getItem("tempFormData");
          if (tempFormDataJSON) {
            const tempFormData = JSON.parse(tempFormDataJSON);
            
            // Create a combined campaign data object with both form data and AI predictions
            currentCampaignData = {
              campaignName: tempFormData.businessName || "Your Campaign",
              businessName: tempFormData.businessName || "Your Business",
              industry: tempFormData.industry || "General",
              budget: tempFormData.budget || 25000,
              duration: tempFormData.campaignDuration || "7 days",
              startDate: tempFormData.startDate,
              targetAudience: tempFormData.targetAudience || "General Public",
              location: tempFormData.location || "Your City",
              objectives: tempFormData.objectives || [],
              aiPredictions: previewAnalytics.data,
              isPreview: true
            };
            
            // Generate realistic locations based on the industry
            const industry = tempFormData.industry || "General";
            const cityLocations = {
              "Retail": ["Shopping District", "Main Street Mall", "Downtown Plaza"],
              "Food & Beverage": ["Restaurant Row", "Nightlife District", "Food Festival Area"],
              "Technology": ["Tech Hub", "Innovation District", "University Area"],
              "Healthcare": ["Medical District", "Wellness Boulevard", "Health Center Area"],
              "Education": ["University Campus", "School District", "Library Center"],
              "Entertainment": ["Theater District", "Cinema Complex", "Sports Arena Area"],
              "General": ["City Center", "Business District", "Main Boulevard"]
            };
            
            // Select locations based on industry or use defaults
            const industryLocations = cityLocations[industry] || cityLocations["General"];
            
            // Generate example locations
            currentCampaignData.locations = [
              { name: industryLocations[0], description: "Primary target area with high visibility" },
              { name: industryLocations[1], description: "Secondary area with strong target audience presence" },
              { name: previewAnalytics.data.bestPerformingAreas?.high_traffic || "High Traffic Area", 
                description: "AI-recommended area with maximum impression potential" }
            ];
            
            // Generate example timings
            currentCampaignData.timings = [
              { time: previewAnalytics.data.bestPerformingTimes?.weekday_morning || "8:00 AM - 11:00 AM", 
                description: "Morning commute hours with strong visibility" },
              { time: previewAnalytics.data.bestPerformingTimes?.weekday_evening || "5:00 PM - 8:00 PM", 
                description: "Evening rush hour for maximum exposure" },
              { time: previewAnalytics.data.bestPerformingTimes?.weekend || "11:00 AM - 4:00 PM", 
                description: "Weekend peak hours for shopping and leisure traffic" }
            ];
            
            // Generate visibility heatmap
            currentCampaignData.visibilityHeatmap = [
              { time: "Morning (8-11 AM)", visibility: 75 },
              { time: "Midday (11 AM-2 PM)", visibility: 60 },
              { time: "Afternoon (2-5 PM)", visibility: 50 },
              { time: "Evening (5-8 PM)", visibility: 85 },
              { time: "Night (8-11 PM)", visibility: 70 }
            ];
            
            // Generate route plans
            currentCampaignData.routePlanDays13 = [
              { time: "8:00 AM", location: `Start at ${industryLocations[0]}` },
              { time: "10:30 AM", location: `Move to ${previewAnalytics.data.bestPerformingAreas?.high_traffic}` },
              { time: "12:30 PM", location: "Lunch break at popular area" },
              { time: "2:00 PM", location: `Continue to ${industryLocations[1]}` }
            ];
            
            currentCampaignData.routePlanDays45 = [
              { time: "11:00 AM", location: `Start at ${industryLocations[1]}` },
              { time: "1:30 PM", location: `Move to ${previewAnalytics.data.bestPerformingAreas?.shopping}` },
              { time: "4:00 PM", location: `Continue to ${previewAnalytics.data.bestPerformingAreas?.business}` }
            ];
            
            currentCampaignData.routePlanDays67 = [
              { time: "12:00 PM", location: `Weekend route: Start at ${industryLocations[0]}` },
              { time: "3:00 PM", location: `Move to ${previewAnalytics.data.bestPerformingAreas?.high_traffic}` },
              { time: "6:00 PM", location: "Evening positioning in entertainment district" }
            ];
            
            // Generate a professional-looking route plan
            const routePlan = {
              optimalRoutes: [
                {
                  name: "Route A: Primary Visibility",
                  waypoints: [
                    {
                      location: industryLocations[0],
                      duration: "2 hours daily",
                      impressions: Math.round(previewAnalytics.data.totalImpressions * 0.4 / 30)
                    },
                    {
                      location: previewAnalytics.data.bestPerformingAreas.high_traffic,
                      duration: "3 hours daily",
                      impressions: Math.round(previewAnalytics.data.totalImpressions * 0.3 / 30)
                    }
                  ],
                  effectiveness: "92%",
                  priority: "Maximum visibility"
                },
                {
                  name: "Route B: Target Audience Focus",
                  waypoints: [
                    {
                      location: previewAnalytics.data.bestPerformingAreas.shopping,
                      duration: "2.5 hours daily",
                      impressions: Math.round(previewAnalytics.data.totalImpressions * 0.2 / 30)
                    },
                    {
                      location: industryLocations[1],
                      duration: "1.5 hours daily",
                      impressions: Math.round(previewAnalytics.data.totalImpressions * 0.1 / 30)
                    }
                  ],
                  effectiveness: "85%",
                  priority: "Audience targeting"
                }
              ],
              totalRouteDistance: `${Math.round(15 + Math.random() * 10)} km daily`,
              estimatedFuelCost: `â‚¹${Math.round(500 + Math.random() * 300)} daily`,
              recommendedTiming: previewAnalytics.data.bestPerformingTimes.weekday_evening
            };
            
            // Add the route plan to the campaign data
            currentCampaignData.routePlan = routePlan;
            
            // Clean up localStorage
            localStorage.removeItem("previewAnalytics");
            localStorage.removeItem("tempFormData");
          }
        }
      } else {
        // Handle regular (non-preview) campaign retrieval logic here
        const savedCampaign = localStorage.getItem("savedCampaign");
        if (savedCampaign) {
          currentCampaignData = JSON.parse(savedCampaign);
        } else {
          // Also check for campaignData
          const campaignData = localStorage.getItem("campaignData");
          if (campaignData) {
            const parsedData = JSON.parse(campaignData);
            if (parsedData.formData) {
              // Create campaign data from form data
              currentCampaignData = createCustomizedMockData(parsedData.formData);
              // Clean up localStorage
              localStorage.removeItem("campaignData");
            }
          }
        }
      }
      
      // If no data is found from any source, use mock data as fallback
      if (!currentCampaignData) {
        console.log("No campaign data found, using mock data");
        currentCampaignData = createMockCampaignData();
      }
      
      // Update state with the campaign data
      setCampaignData(currentCampaignData);
    } catch (error) {
      console.error("Error loading campaign data:", error);
      setError(error);
      // Still provide fallback data even on error
      setCampaignData(createMockCampaignData());
    } finally {
      // Always turn off loading state regardless of success or failure
      setLoading(false);
    }
  }, []);

  // Call Gemini API directly from the frontend
  const callGeminiAPI = async (formData) => {
    try {
      // Generate prompt based on form data
      const prompt = generatePrompt(formData)
      console.log("Generated prompt for Gemini:", prompt)

      // Get API key from environment variable or use a hardcoded one for demo
      // In production, you should use environment variables and proper authentication
      const apiKey = "AIzaSyAgBPUp7C4AC5d3DuCADTW4bmi2_7JPAwY" // Replace with your actual API key or use environment variable

      console.log("Calling Gemini API...")

      // Call Gemini API
      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash:generateContent?key=${apiKey}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            contents: [
              {
                parts: [
                  {
                    text: prompt,
                  },
                ],
              },
            ],
          }),
        },
      )

      if (!response.ok) {
        const errorText = await response.text()
        console.error("Gemini API error response:", errorText)
        throw new Error(`Gemini API error: ${response.status} - ${errorText}`)
      }

      const data = await response.json()
      console.log("Gemini API response received:", data)

      // Log the actual text content for easier debugging
      if (data.candidates && data.candidates.length > 0) {
        const textContent = data.candidates[0].content.parts[0].text
        console.log("Gemini API text response:", textContent)
      }

      return data
    } catch (error) {
      console.error("Error calling Gemini API:", error)
      // Return null or a default response in case of error
      return null
    }
  }

  // Generate prompt for Gemini API based on form data
  const generatePrompt = (formData) => {
    const objectivesStr =
      formData.objectives && formData.objectives.length > 0
        ? formData.objectives.map(mapObjective).join(", ")
        : "Brand Awareness"

    return `Generate a mobile billboard route plan with these specifications:

Business: ${formData.businessName || ""}
Industry: ${formData.industry || ""}
Target Audience: ${formData.targetAudience || ""}
Campaign Objectives: ${objectivesStr}
Location: ${formData.location || ""}
Duration: ${formData.campaignDuration || ""}
Budget: â‚¹${formData.budget || 0}

Provide:
1. Recommended locations with timing windows
2. Optimal routes for maximum visibility
3. Estimated impressions per day
4. Any special considerations
Format the response with clear sections and bullet points.`
  }

  // Map objective codes to readable text
  const mapObjective = (objective) => {
    switch (objective) {
      case "brand":
        return "Brand Awareness"
      case "traffic":
        return "Store Traffic"
      case "launch":
        return "Product Launch"
      case "event":
        return "Event Promotion"
      default:
        return objective
    }
  }

  // Process Gemini API response
  const processGeminiResponse = (response, formData) => {
    console.log("Processing Gemini API response...")

    // Start with a customized data structure based on the form data
    const result = createCustomizedMockData(formData)

    // If Gemini API call failed, return the mock data
    if (!response || !response.candidates || response.candidates.length === 0) {
      console.log("No valid Gemini response, using mock data")
      return result
    }

    try {
      // Extract text from Gemini response
      const candidate = response.candidates[0]
      const content = candidate.content
      const part = content.parts[0]
      const text = part.text

      console.log("Extracted text from Gemini response")

      // Add the AI-generated route plan
      result.routePlan = text

      // Parse the AI response to extract locations, timings, etc.
      console.log("Parsing AI response to extract structured data...")
      const parsedData = parseAIResponse(text)
      console.log("Parsed data from AI response:", parsedData)

      // Merge the parsed data with our result
      const finalResult = { ...result, ...parsedData }
      console.log("Final processed data:", finalResult)

      return finalResult
    } catch (error) {
      console.error("Error processing Gemini response:", error)
      return result
    }
  }

  // Helper function to parse the raw route plan text into structured data
  const parseCampaignData = (data) => {
    // Default structure that matches what the component expects
    const defaultData = {
      businessName: "Your Business",
      businessType: "Retail",
      targetAudience: "18-40 year olds",
      campaignType: "Brand Awareness",
