  Clock,
  Building,
  MessageSquare,
  CheckCircle,
  ArrowRight,
  Loader2,
  Phone,
} from "lucide-react"
import { Button } from "../components/ui/button"
import { Card, CardContent } from "../components/ui/card"
import { Input } from "../components/ui/input"
import { Label } from "../components/ui/label"
import { Textarea } from "../components/ui/textarea"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../components/ui/select"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "../components/ui/tabs"
import { Slider } from "../components/ui/slider"

export default function PlanPage() {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    businessName: "",
    industry: "",
    targetAudience: "",
    location: "",
    campaignDuration: "",
    budget: 1000,
    startDate: "",
    additionalInfo: "",
    objectives: [],
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSliderChange = (value) => {
    setFormData((prev) => ({ ...prev, budget: value[0] }));
  };

  const handleObjectiveToggle = (objective) => {
    setFormData((prev) => ({
      ...prev,
      objectives: prev.objectives.includes(objective)
        ? prev.objectives.filter((o) => o !== objective)
        : [...prev.objectives, objective]
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('http://localhost:8080/api/chat/generateRoute', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Failed to submit form');
      }

      const data = await response.json();
      navigate('/campaign-results', { state: { campaignData: data } });
    } catch (error) {
      console.error('Submission error:', error);
      setIsSubmitting(false);
    }
  };

  const objectives = [
    { id: "brand", label: "Brand Awareness", icon: Building },
    { id: "traffic", label: "Store Traffic", icon: Users },
    { id: "launch", label: "Product Launch", icon: Target },
    { id: "event", label: "Event Promotion", icon: Calendar },
  ];

  const nextStep = () => setCurrentStep((prev) => Math.min(prev + 1, 3));
  const prevStep = () => setCurrentStep((prev) => Math.max(prev - 1, 1));

  return (
    <div className="py-12 bg-gradient-to-b from-primary/30 to-white min-h-screen">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <div className="text-center mb-10">
            <h1 className="text-3xl md:text-4xl font-bold mb-4">Plan Your Mobile Billboard Campaign</h1>
            <p className="text-lg text-gray-600">
              Tell us about your business and goals, and our AI will create the perfect route plan
            </p>
          </div>

          <Card className="border-primary/20">
            <CardContent className="pt-6">
              <form onSubmit={handleSubmit}>
                {/* Step 1: Business Information */}
                {currentStep === 1 && (
                  <div className="space-y-6 animate-fade-in">
                    <h2 className="text-xl font-bold mb-4">Tell us about your business</h2>
                    <div className="space-y-4">
                      <div>
                        <Label htmlFor="businessName">Business Name</Label>
                        <Input
                          id="businessName"
                          name="businessName"
                          value={formData.businessName}
                          onChange={handleInputChange}
                          placeholder="Your Business Name"
                          required
                        />
                      </div>
                      <div>
                        <Label htmlFor="industry">Industry</Label>
                        <Select
                          value={formData.industry}
                          onValueChange={(value) => setFormData(prev => ({ ...prev, industry: value }))}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Select your industry" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="retail">Retail</SelectItem>
                            <SelectItem value="restaurant">Restaurant/Food Service</SelectItem>
                            <SelectItem value="entertainment">Entertainment</SelectItem>
                            <SelectItem value="healthcare">Healthcare</SelectItem>
                            <SelectItem value="education">Education</SelectItem>
                            <SelectItem value="technology">Technology</SelectItem>
                            <SelectItem value="automotive">Automotive</SelectItem>
                            <SelectItem value="financial">Financial Services</SelectItem>
                            <SelectItem value="other">Other</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                      <div>
                        <Label htmlFor="targetAudience">Target Audience</Label>
                        <Textarea
                          id="targetAudience"
                          name="targetAudience"
                          value={formData.targetAudience}
                          onChange={handleInputChange}
                          placeholder="Describe your ideal customers"
                          required
                        />
                      </div>
                    </div>
                    <div className="flex justify-end">
                      <Button type="button" onClick={nextStep}>
                        Next Step <ArrowRight className="ml-2 h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                )}

                {/* Step 2: Campaign Goals */}
                {currentStep === 2 && (
                  <div className="space-y-6 animate-fade-in">
                    <h2 className="text-xl font-bold mb-4">What are your campaign objectives?</h2>
                    <div className="space-y-4">
                      <Label>Select all that apply</Label>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        {objectives.map((objective) => (
                          <div
                            key={objective.id}
                            onClick={() => handleObjectiveToggle(objective.id)}
                            className={`p-4 border rounded-md cursor-pointer flex items-center ${
                              formData.objectives.includes(objective.id)
                                ? "border-primary bg-primary/10"
                                : "border-gray-200 hover:border-primary/50"
                            }`}
                          >
                            <div className={`w-10 h-10 rounded-full flex items-center justify-center mr-3 ${
                              formData.objectives.includes(objective.id) ? "bg-primary" : "bg-gray-100"
                            }`}>
                              <objective.icon className={
                                formData.objectives.includes(objective.id) ? "text-white" : "text-gray-500"
                              } />
                            </div>
                            <span className="font-medium">{objective.label}</span>
                            {formData.objectives.includes(objective.id) && (
                              <CheckCircle className="ml-auto h-5 w-5 text-primary" />
                            )}
                          </div>
                        ))}
                      </div>
                      <div>
                        <Label htmlFor="additionalInfo">Additional Campaign Details</Label>
                        <Textarea
                          id="additionalInfo"
                          name="additionalInfo"
                          value={formData.additionalInfo}
                          onChange={handleInputChange}
                          placeholder="Any additional details"
                        />
                      </div>
                    </div>
                    <div className="flex justify-between">
                      <Button type="button" variant="outline" onClick={prevStep}>
                        Previous
                      </Button>
                      <Button type="button" onClick={nextStep}>
                        Next Step <ArrowRight className="ml-2 h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                )}

                {/* Step 3: Location & Budget */}
                {currentStep === 3 && (
                  <div className="space-y-6 animate-fade-in">
                    <h2 className="text-xl font-bold mb-4">Where and when do you want to advertise?</h2>
                    <div className="space-y-4">
                      <div>
                        <Label htmlFor="location">Target Location</Label>
                        <Input
                          id="location"
                          name="location"
                          value={formData.location}
                          onChange={handleInputChange}
                          placeholder="City or neighborhood"
                          required
                        />
                      </div>
                      <div>
                        <Label htmlFor="campaignDuration">Campaign Duration</Label>
                        <Select
                          value={formData.campaignDuration}
                          onValueChange={(value) => setFormData(prev => ({ ...prev, campaignDuration: value }))}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Select duration" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="1-day">1 Day</SelectItem>
                            <SelectItem value="1-week">1 Week</SelectItem>
                            <SelectItem value="2-weeks">2 Weeks</SelectItem>
                            <SelectItem value="1-month">1 Month</SelectItem>
                            <SelectItem value="3-months">3 Months</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                      <div>
                        <Label htmlFor="startDate">Start Date</Label>
                        <Input
                          id="startDate"
                          name="startDate"
                          type="date"
                          value={formData.startDate}
                          onChange={handleInputChange}
                          required
                        />
                      </div>
                      <div className="space-y-2">
                        <div className="flex justify-between">
                          <Label htmlFor="budget">Budget (INR)</Label>
                          <span className="font-medium">₹{formData.budget.toLocaleString()}</span>
                        </div>
                        <Slider
                          id="budget"
                          min={1000}
                          max={50000}
                          step={1000}
                          value={[formData.budget]}
                          onValueChange={handleSliderChange}
                        />
                        <div className="flex justify-between text-sm text-gray-500">
                          <span>₹1,000</span>
                          <span>₹50,000+</span>
                        </div>
                      </div>
                    </div>
                    <div className="flex justify-between">
                      <Button type="button" variant="outline" onClick={prevStep}>
                        Previous
                      </Button>
                      <Button type="submit" disabled={isSubmitting}>
                        {isSubmitting ? (
                          <>
                            <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                            Processing...
                          </>
                        ) : (
                          "Submit"
                        )}
                      </Button>
                    </div>
                  </div>
                )}
              </form>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}
