"use client"
import { useState, useEffect } from "react"
import { MapPin } from "lucide-react"

export default function CampaignMap({ locations }) {
  const [isMounted, setIsMounted] = useState(false)
  const [isMapVisible, setIsMapVisible] = useState(true)

  useEffect(() => {
    setIsMounted(true)
  }, [])

  if (!isMounted) {
    return (
      <div className="h-[400px] rounded-lg border bg-card flex items-center justify-center">
        <div className="text-muted-foreground">Loading map...</div>
      </div>
    )
  }

  // Use the actual locations data passed from the parent component
  const locationData = locations || [
    { name: "Default Location 1", description: "No locations provided" },
    { name: "Default Location 2", description: "No locations provided" },
  ]

  // Extract location names for display
  const locationNames = locationData.map((loc) => loc.name)

  return (
    <div className="h-[400px] rounded-lg border bg-card overflow-hidden relative">
      {/* This is a placeholder for a real map. In a production app, you would integrate with a mapping library */}
      <div className="absolute inset-0 bg-[#f0f0f0] flex flex-col items-center justify-center p-4">
        <div className="text-center mb-4">
          <h3 className="font-semibold text-lg">Mobile Billboard Route</h3>
          <p className="text-sm text-muted-foreground">Interactive map showing optimal locations</p>
          <button onClick={() => setIsMapVisible(!isMapVisible)} className="mt-2 text-xs text-primary underline">
            {isMapVisible ? "Hide Map" : "Show Map"}
          </button>
        </div>

        {isMapVisible && (
          <div className="relative w-full max-w-md h-64">
            {/* Simplified map representation */}
            <div className="absolute inset-0 bg-[#e0e0e0] rounded-lg shadow-inner">
              {/* Main roads */}
              <div className="absolute top-1/2 left-0 right-0 h-2 bg-white"></div>
              <div className="absolute top-0 bottom-0 left-1/2 w-2 bg-white"></div>
              <div className="absolute top-1/4 left-0 right-0 h-1 bg-[#d0d0d0]"></div>
              <div className="absolute top-3/4 left-0 right-0 h-1 bg-[#d0d0d0]"></div>
              <div className="absolute top-0 bottom-0 left-1/4 w-1 bg-[#d0d0d0]"></div>
              <div className="absolute top-0 bottom-0 left-3/4 w-1 bg-[#d0d0d0]"></div>

              {/* Location markers - dynamically positioned based on index */}
              {locationData.map((location, index) => {
                // Calculate positions based on index to spread markers around
                const positions = [
                  { top: "20%", left: "30%" },
                  { top: "40%", left: "60%" },
                  { top: "70%", left: "40%" },
                  { top: "30%", left: "80%" },
                  { top: "50%", left: "70%" },
                  { top: "10%", left: "50%" },
                  { top: "80%", left: "70%" },
                  { top: "25%", left: "15%" },
                ]

                // Use modulo to cycle through positions if we have more locations than positions
                const position = positions[index % positions.length]

                return (
                  <div
                    key={index}
                    className="absolute flex flex-col items-center"
                    style={{ top: position.top, left: position.left }}
                  >
                    <div className="w-6 h-6 bg-primary rounded-full flex items-center justify-center shadow-md">
                      <MapPin className="h-4 w-4 text-white" />
                    </div>
                    <div className="mt-1 px-1.5 py-0.5 bg-white text-[10px] rounded shadow whitespace-nowrap">
                      {location.name}
                    </div>
                  </div>
                )
              })}

              {/* Route lines connecting markers */}
              <svg className="absolute inset-0 w-full h-full" style={{ pointerEvents: "none" }}>
                <path
                  d="M80,80 L180,130 L160,220 L240,120"
                  fill="none"
                  stroke="#4f46e5"
                  strokeWidth="2"
                  strokeDasharray="5,5"
                />
              </svg>
            </div>
          </div>
        )}

        <div className="mt-4 text-sm text-center">
          <div className="font-medium mb-2">Recommended Locations:</div>
          <ul className="list-disc list-inside">
            {locationNames.map((name, index) => (
              <li key={index} className="text-xs">
                {name}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  )
}
